note_models.css:
.card {
  font-family: arial;
  font-size: 20px;
  text-align: center;
  color: black;
  background-color: white;
}

.small {
  font-size: 15px;
}

table, td, th {
  border-collapse: collapse;
  padding: 5px;
}

table {
  display: inline-block;
  text-align: left;
}

label {
  margin-left: 0.4em;
}

.correct {
  background-color: lime;
}

.nightMode .correct {
  background-color: #009900;
}

.wrong {
  background-color: OrangeRed;
}

.hidden {
  /*
  This block is from Glutanimate's Cloze Overlapper card template.
  The Cloze Overlapper card template is licensed under the CC BY-SA 4.0
  license (https://creativecommons.org/licenses/by-sa/4.0/).
  */
  /* guarantees a consistent width across front and back */
  font-weight: bold;
  display: block;
  line-height: 0;
  height: 0;
  overflow: hidden;
  visibility: hidden;
}

.card {
	font-family: MyFont;
	font-size: 23px;
	text-align: center;
	color: #142850;
	background: #f9fbfe;
	direction: rtl;
	max-width: 600px;
	margin: 35px auto;
	padding: 0 15px;
}

.card.nightMode {
	background: #232931;
	color: #eeeeee;
}

.cloze {
	color: #00909e;
}

.nightMode .cloze {
	color: #fd7014;
}

.extra {
	color: #27496d;
	font-size: 20px;
}

.nightMode .extra {
	color: #dae1e7;
}

@font-face {
	font-family: MyFont;
	font-weight: 500;
	src: url('_Sh_LoutsSh.ttf');
}

.card {
    font-family: MyFont, sans-serif;
	font-size: 23px; /*هذا الرقم خاص بتغيير حجم الخط*/
	max-width: 620px;
	background-color: #fffff9;
	direction: rtl;
	margin: 5px auto;
	text-align: justify; /*لتوسيط النصوص غير الكلمة بعد النقطتين إلى center*/
	padding: 0 5px;
	line-height: 1.8em;
}

.card.nightMode { 
    background: #555;
    color:#eee;
}

.alert {
    position: relative;
    padding: 15px;
    margin-bottom:5px;
    border-radius: .25rem;
}

.front  {
    color: #004085;
    background: #cce5ff;
}

.nightMode .front {
    background: #476d7c;
	color: #fff;
}

.back {
    color: #155724;
    background: #d4edda;
}

.nightMode .back {
	background: #254b62;
	color: #fff;
}

.extra {
    color: #856404;
    background: #fff3cd;
}

.nightMode .extra {
	background: #1d3e53;
	color: #fff;
}

@font-face {
	font-family: MyFont;
	font-weight: 500;
	src: url('_Sh_LoutsSh.ttf'); 
}

@font-face {
	font-family: MyFont;
	font-weight: 700;
	src: url('_Sh_LoutsShB.ttf'); 
}
